import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const getAllAgents = createAsyncThunk("agents/getAllAgents", async (body) => {
    try {
        const response = await axios.post(`https://aro6clc3xi.execute-api.us-east-2.amazonaws.com/dev/signin`, body);
        console.log('get all agent api response:- ', response)
        let obj = {
            status: 200, ...response.data
        }
        return obj;
    } catch (err) {
        console.log(err.response.data);
        let obj = {
            status: 500, ...err.response.data
        }
        return obj;
    }
});

export const signUpUser = createAsyncThunk("users/signUpUser", async (userDetails) => {
    try {
        const response = await axios.post(`https://aro6clc3xi.execute-api.us-east-2.amazonaws.com/dev/signup`, userDetails);
        console.log('Sign up api response:- ', response)
        return response.data;
    } catch (err) {
        console.log(err);
    }
});



const agentSlice = createSlice({
    name: "agent",
    initialState: {
        loading: false,
        agentList: []
    },
    reducers: {
    },
    extraReducers: {
        [getAllAgents.pending]: (state, action) => {
            state.loading = true;
        },
        [getAllAgents.fulfilled]: (state, action) => {
            if (action.payload.status == 200) {
                state.loading = false;
                console.log(action.payload);
            } else {
                state.loading = false;
            }

        },
        [getAllAgents.rejected]: (state, action) => {
            state.loading = false;
        },
    },
});

// export const { setUserDetailsFromSS, logoutTheUser } = agentSlice.actions;

export default agentSlice.reducer;
