import React, { useState, useContext, useEffect } from "react";
import { AccountContext } from "../Account";
import { Dropdown } from 'react-bootstrap';
import UserPool from "../UserPool";
import { CognitoUser, AuthenticationDetails } from "amazon-cognito-identity-js";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  changeLoadingState,
  changeLoginState,
} from "../reducers/CommonReducer";
function Navbar() {
  const dispatch = useDispatch();
  const { loginState } = useSelector(state => state.common); 
  const [isUserLoggedIn, setIsUserLoggedIn] = useState(false);
  const { getSession, logout } = useContext(AccountContext);

  useEffect(() => {
    if (getSession) {
      getSession()
        .then((session) => {
          console.log("session ", session);
          setIsUserLoggedIn(true);
        })
        .catch((err) => setIsUserLoggedIn(false));
    }
  }, [loginState]);

  const handleLogout = () => {
    logout();
    changeLoginState(false);
  };

  return (
    <nav className="navbar navbar-expand-md navbar-dark bg-dark">
      <div className="container">
        <a className="navbar-brand" href="#">
          My-App
        </a>
        <div className="collapse navbar-collapse" id="navbarToggleDemo2">
          {isUserLoggedIn ? (
            <div className="d-flex ml-auto">
              <Dropdown className="ml-auto">
                <Dropdown.Toggle variant="light" id="dropdown-basic">
                  Prateek
                </Dropdown.Toggle>

                <Dropdown.Menu>
                  {/* <Dropdown.Item href="/addresses">
                    Manage Addresses
                  </Dropdown.Item> */}
                  <Dropdown.Item onClick={handleLogout} href="/">
                    Logout
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </div>
          ) : (
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link className="nav-link lightBoldFont" to="/sign-in">
                  Log In
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link lightBoldFont" to="/sign-up">
                  Sign up
                </Link>
              </li>
            </ul>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
