import "./AgentList.css";
import React, { useEffect, useState } from "react";
import { faTrash, faPencilAlt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useDispatch, useSelector } from "react-redux";
import { Button } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import { Grid, GridColumn } from "@progress/kendo-react-grid";
import {
  getAllAgents,
  loadAgentsFromHR,
  deleteAgent,
  fetchLicenseInformation,
} from "../../reducers/AgentReducer";
const DetailComponent = (props) => {
  const dataItem = props.dataItem;
  return (
    <section>
      <p>
        <strong>In Stock:</strong> {dataItem.email} units
      </p>
      <p>
        <strong>On Order:</strong> {dataItem.email} units
      </p>
      <p>
        <strong>Reorder Level:</strong> {dataItem.email} units
      </p>
      <p>
        <strong>Discontinued:</strong> {dataItem.email}
      </p>
    </section>
  );
};
function AgentList() {
  const history = useHistory();
  const dispatch = useDispatch();
  const { agentList } = useSelector((state) => state.agent);
  useEffect(() => {
    dispatch(getAllAgents());
  }, []);

  const handleLoadDataFromHr = () => {
    console.log("handleLoadDataFromHr clicked");
    dispatch(loadAgentsFromHR());
  };
  const updateLicenseInformation = () => {
    console.log("updateLicenseInformation clicked");
    dispatch(fetchLicenseInformation());
  };
  const deleteAgentButtonHandler = (email) => {
    console.log("delete button clicked for email:- " + email);
    dispatch(deleteAgent(email));
  };
  const editAgentButtonHandler = (email) => {
    console.log("edit button clicked for email:- " + email);
    history.push(`/edit-agent/${email}`);
  };
  // const expandChange = (event) => {
  //   let newData = data.map((item) => {
  //     if (item.email === event.dataItem.email) {
  //       item.expanded = !event.dataItem.expanded;
  //     }

  //     return item;
  //   });
  //   setData(newData);
  // };
  return (
    <div className="p-3">
      <div
        className="col-md-12 p-0 m-0 mb-2 d-flex"
        style={{ justifyContent: "space-between" }}
      >
        <div>
          <h4 className="m-0">Agent List</h4>
        </div>
        <div>
          <Button
            variant="primary"
            className="mr-2"
            size="sm"
            type="button"
            onClick={handleLoadDataFromHr}
          >
            Get Agents from HR
          </Button>
          <Button
            variant="primary"
            className="mr-2"
            size="sm"
            type="button"
            onClick={updateLicenseInformation}
          >
            Update License Information
          </Button>
          <Button variant="primary" size="sm" type="button">
            Add new Agent
          </Button>
        </div>
      </div>
      <Grid
        detail={DetailComponent}
        data={agentList}
        style={{
          height: "500px",
          width: "100%",
        }}
        reorderable={true}
        resizable={true}
        rowHeight={36}
        expandField="expanded"
        // onExpandChange={expandChange}
      >
        <GridColumn
          field="name"
          title="Name"
          headerClassName="headerClassName1"
          width={175}
        />
        <GridColumn
          field="email"
          title="Email"
          headerClassName="headerClassName1"
          width={250}
        />
        <GridColumn
          field="dob"
          title="DOB"
          cell={(props) => (
            <td>
              {props.dataItem.dob
                ? new Date(props.dataItem.dob).toDateString().slice(4)
                : null}
            </td>
          )}
          headerClassName="headerClassName1"
          width={200}
        />
        <GridColumn
          field="crmId"
          title="CRM ID"
          headerClassName="headerClassName1"
        />
        <GridColumn
          field="teleCallingId"
          title="Tele calling Id"
          headerClassName="headerClassName1"
        />
        <GridColumn
          field="address"
          title="Address"
          headerClassName="headerClassName1"
        />
        <GridColumn
          field="licenseUrnNo"
          title="License URN"
          headerClassName="headerClassName1"
        />
        <GridColumn
          field="licenseExpiryDate"
          title="License Expiry Date"
          cell={(props) => (
            <td>
              {props.dataItem.licenseExpiryDate
                ? new Date(props.dataItem.licenseExpiryDate)
                    .toDateString()
                    .slice(4)
                : null}
            </td>
          )}
          headerClassName="headerClassName1"
        />
        <GridColumn
          field="licenseIssueDate"
          title="License Issue Date"
          cell={(props) => (
            <td>
              {props.dataItem.licenseExpiryDate
                ? new Date(props.dataItem.licenseIssueDate)
                    .toDateString()
                    .slice(4)
                : null}
            </td>
          )}
          headerClassName="headerClassName1"
        />
        <GridColumn
          title="Actions"
          cell={(props) => (
            <td>
              {" "}
              <FontAwesomeIcon
                className="cursor-pointer mr-3"
                icon={faPencilAlt}
                onClick={() => editAgentButtonHandler(props.dataItem.email)}
              />
              {"   "}
              <FontAwesomeIcon
                className="cursor-pointer"
                icon={faTrash}
                onClick={() => deleteAgentButtonHandler(props.dataItem.email)}
              />
            </td>
          )}
          headerClassName="headerClassName1"
        />
      </Grid>
    </div>
  );
}

export default AgentList;
