import "./GraphVendor.css";
import React, { useEffect, useState } from "react";
import { faTrash, faPencilAlt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useDispatch, useSelector } from "react-redux";
import { Form, Row, Col, Button, InputGroup } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import {
  Chart,
  ChartLegend,
  ChartSeries,
  ChartSeriesItem,
  ChartTitle,
} from "@progress/kendo-react-charts";
import "hammerjs";

import { fetchDataByInsuranceType } from "../../../reducers/DashboardReducer";
import { fetchInsurancePolicyTypes } from "../../../reducers/InsuranceTypeVendorReducer";
function GraphVendor() {
  const history = useHistory();
  const dispatch = useDispatch();
  const { loading, dataByInsuranceType } = useSelector(
    (state) => state.dashboard
  );
  const insuranceTypeVendor = useSelector((state) => state.insuranceTypeVendor);
  const [dataByInsuranceType1, setDataByInsuranceType1] = useState([]);
  const [insuranceTypeId, setInsuranceTypeId] = useState("");
  useEffect(() => {
    dispatch(fetchDataByInsuranceType());
    dispatch(fetchInsurancePolicyTypes());
  }, []);

  useEffect(() => {
    if (!loading && dataByInsuranceType) {
      setDataByInsuranceType1(dataByInsuranceType);
    }
  }, [loading, dataByInsuranceType]);

  return (
    <div className="p-3">
      <Chart>
        <ChartTitle text="Count of insurance sold distributed by insurance type" />
        <Form>
          <Form.Group as={Row} controlId="exampleForm.ControlSelect1">
            <Form.Label column sm={2}>
              Insurance Type
            </Form.Label>
            <Col sm={10} className="pr-5">
              <Form.Control
                required
                as="select"
                onChange={(event) => setInsuranceTypeId(event.target.value)}
              >
                <option value={null}>Please select insurance type</option>
                {insuranceTypeVendor &&
                  insuranceTypeVendor.insuranceType &&
                  insuranceTypeVendor.insuranceType.map((item) => (
                    <option
                      value={item.insuranceTypeId}
                      key={item.insuranceTypeId}
                    >
                      {item.insuranceTypeName}
                    </option>
                  ))}
              </Form.Control>
            </Col>
          </Form.Group>
        </Form>
        <ChartLegend position="bottom" />
        <ChartSeries>
          <ChartSeriesItem
            type="pie"
            data={dataByInsuranceType1}
            field="totalCount"
            categoryField="insuranceTypeName"
            labels={{
              visible: true,
              content: (props) => {
                return `${props.dataItem.insuranceTypeName} : ${props.dataItem.totalCount}`;
              },
            }}
          />
        </ChartSeries>
      </Chart>
    </div>
  );
}

export default GraphVendor;
